mapper.select:
select
id, user_id, file_id, permission, last_open_time
from file_permission


        and id = #{id}


        and user_id = #{userId}


        and file_id = #{fileId}


        and permission = #{permission}


        and last_open_time = #{lastOpenTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from file_permission


        and id = #{id}


        and user_id = #{userId}


        and file_id = #{fileId}


        and permission = #{permission}


        and last_open_time = #{lastOpenTime}
